button_clientct_effect_isactive.script:
function onClickRelease(button, x, y)
	local result = super and super.onClickRelease and super.onClickRelease(button, x, y);
	local sLabel = window.label.getValue();
	if (getValue() == 1) and (sLabel:match("SIZE%:") or sLabel:match("SPACE%:") or sLabel:match("REACH%:")) then
		setValue(0);
		result = true;
	end
	return result;
end
